# Convert to DataFrame
df = pd.DataFrame(data)

# Clean the data
df = df[df["Season Length"] != "-"]
df["Season Length"] = df["Season Length"].astype(int)

# Plot 1: Season Length Over Time
plt.figure(figsize=(12, 6))
plt.plot(df["Year"], df["Season Length"], marker='o', linestyle='-', color='b')
plt.title("Season Length Over Time")
plt.xlabel("Year")
plt.ylabel("Season Length (days)")
plt.grid(True)
plt.show()

# Plot 2: First and Last Frost Dates Over Time
plt.figure(figsize=(12, 6))
plt.scatter(df["Year"], pd.to_datetime(df["Last"].str.extract(r'(\d{2}-\d{2})')[0]), label="Last Frost", color='r')
plt.scatter(df["Year"], pd.to_datetime(df["First"].str.extract(r'(\d{2}-\d{2})')[0]), label="First Frost", color='g')
plt.title("First and Last Frost Dates Over Time")
plt.xlabel("Year")
plt.ylabel("Date")
plt.legend()
plt.grid(True)
plt.show()

# Plot 3: Distribution of Season Length
plt.figure(figsize=(10, 6))
sns.histplot(df["Season Length"], bins=20, kde=True, color='purple')
plt.title("Distribution of Season Length")
plt.xlabel("Season Length (days)")
plt.ylabel("Frequency")
plt.grid(True)
plt.show()